import {Component} from "react";
import {Button} from "react-bootstrap";

class Exemple2 extends Component {

    constructor() {
        super();
        this.state = {
            date: new Date()
        }
    }

    componentDidMount() {
        this.timer = setInterval(() => this.tick(), 1000);
    }

    tick() {
        this.setState({
            date: new Date()
        })
    }

    render() {
        return(
            <div className={"mt-5"}>
                <h2>Clock</h2>
                <p>{ this.state.date.toLocaleTimeString() }</p>
            </div>
        )
    }
}

export default Exemple2;
