import {Component} from "react";
import {Button} from "react-bootstrap";

class Exemple2 extends Component {

    constructor() {
        super();
        this.state = {
            date: new Date(),
        }
    }
    render() {
        return(
            <div>
                <h2>Clock</h2>
                <p>{ this.state.date.toLocaleTimeString() }</p>
            </div>
        )
    }
}

export default Exemple2;
