import Navbar from 'react-bootstrap/Navbar';
import {NavLink} from "react-router-dom";
import {NavDropdown} from "react-bootstrap";
import logo from '../../logo.svg';
import {useState} from "react";
import button from "bootstrap/js/src/button";


function Header() {

    const [theme, setTheme] = useState(false);

    const handleClick = (event) => {
        if (theme === false) {
            event: setTheme(true)
            document.body.classList.add('theme-light');
            document.body.classList.remove('theme-dark');
        } else {
            setTheme(false)
            document.body.classList.add('theme-dark');
            document.body.classList.remove('theme-light');
        }
        console.log(theme)
    }

    return (
        <Navbar variant={"dark"} className={'navbar-padding'}>
            <img src={logo} className="App-logo" alt="logo" />
            <NavLink to="/" className={'item'}>
                Cours React
            </NavLink>
            <NavDropdown title={'Exemples'} className={'item item-dropdown'}>
                <NavDropdown.Item>
                    <NavLink to="/exemple-1">Exemple 1</NavLink>
                </NavDropdown.Item>
                <NavDropdown.Item>
                    <NavLink to="/exemple-2">Exemple 2</NavLink>
                </NavDropdown.Item>
                <NavDropdown.Item>
                    <NavLink to="/exemple-3">Exemple 3</NavLink>
                </NavDropdown.Item>
                <NavDropdown.Item>
                    <NavLink to="/exemple-4">Exemple 4</NavLink>
                </NavDropdown.Item>
            </NavDropdown>
            <NavLink to="/todolist" className={'item'}>
                Todo
            </NavLink>
            <button onClick={handleClick} className={'item'}>
                <i className="fa-solid fa-sun-bright"></i>
            </button>
        </Navbar>
    )
}

export default Header;
