import {useState} from "react";
import TodoForm from "./TodoForm";
import useLocalStorage from "../useLocalStorage";

function TodoList() {

    const {getStorageItem, setItemToStorage} = useLocalStorage();
    const [todoList, setTodoList] = useState(getStorageItem('todoText'));

    const addTodo = todo => {
        console.log(todo)
        if (!todo.text || /^\s*$/.test(todo.text)) return;

        const newTodoList = [todo.text, ...todoList]

        setTodoList(newTodoList);
        setItemToStorage(todoList, 'todoText');
    }

    return (
        <div>
            <TodoForm onSubmit={addTodo}></TodoForm>
            {todoList.map((listItem, index) => (
                <p key={index} name={listItem}>{listItem}</p>
            ))}
        </div>
    )

}

export default TodoList;
