import {Fragment, useState} from "react";
import TodoForm from "./TodoForm";
import useLocalStorage from "../useLocalStorage";

function TodoList() {

    const { getStoredItem, setItemToStorage } = useLocalStorage();
    const [todoList, setTodoList] = useState(getStoredItem('todoText') || []);
    const [isEditing, setIsEditing] = useState(false);
    const [currentTodo, setCurrentTodo] = useState({});

    const addTodo = todo => {
        console.log(todo)
        if (!todo.text || /^\s*$/.test(todo.text)) return;

        const newTodoList = [todo.text, ...todoList]

        setTodoList(newTodoList);
        setItemToStorage(newTodoList, 'todoText');
    }

    const handleUpdate = (event, index) => {

    }

    const handleDelete = (event, index) => {
        const todoTemp = todoList.slice();
        todoTemp.splice(index, 1);
        setTodoList(todoTemp);
        setItemToStorage(todoTemp, 'todoText');
    }

    return (
        <div>
            <TodoForm onSubmit={addTodo}></TodoForm>
            {todoList.map((listItem, index) => (
                <TodoLine
                 key={index}
                 index={index}
                 listItem={listItem}
                 handleUpdate={handleUpdate}
                 handleDelete={handleDelete}
                />
            ))}
        </div>
    )

}

const TodoLine = ({index, listItem, handleUpdate, handleDelete}) => {
    return(
        <>
            <p key={index} name={listItem}>{listItem}</p>
            <button onClick={event => {handleUpdate(event, index)}}>Update</button>
            <button onClick={event => {handleDelete(event, index)}}>Delete</button>
        </>
    )
}

export default TodoList;
